  float f=0.0;
  if(cc_time.x-floor(cc_time.x) < 0.5){
    f = cc_time.x-floor(cc_time.x);
  }else{
    f = 1.0-(cc_time.x-floor(cc_time.x));
  }
  vec4 o = vec4(f,0.0,0.0,0.5);
